import React, { useState } from 'react';
import './CostCalculation.css';  // Import the correct CSS file

const CostCalculation = () => {
  const [areaSize, setAreaSize] = useState('');
  const [constructionType, setConstructionType] = useState('Grey Structure');
  const [material, setMaterial] = useState('Without Material');
  const [cost, setCost] = useState(null);

  const handleCalculate = () => {
    const baseCost = constructionType === 'Complete' ? 3000 : 2000;
    const calculatedCost = areaSize * baseCost;
    setCost(calculatedCost);
  };

  return (
    <div className="cost-calculation-page">
      <h1>Construction Cost Calculator</h1>
      <div className="cost-form">
        <div className="input-group">
          <label>Area Size (Sq. Ft.)</label>
          <input 
            type="number" 
            value={areaSize} 
            onChange={(e) => setAreaSize(e.target.value)} 
            placeholder="Enter the area size"
          />
        </div>
        <div className="input-group">
          <label>Construction Type</label>
          <select value={constructionType} onChange={(e) => setConstructionType(e.target.value)}>
            <option value="Grey Structure">Grey Structure</option>
            <option value="Complete">Complete</option>
          </select>
        </div>
        <div className="input-group">
          <label>Material</label>
          <select value={material} onChange={(e) => setMaterial(e.target.value)}>
            <option value="With Material">With Material</option>
            <option value="Without Material">Without Material</option>
          </select>
        </div>
        <button onClick={handleCalculate} className="calculate-btn">Calculate Cost</button>

        {cost && <div className="result">Estimated Cost: {cost} PKR</div>}
      </div>
    </div>
  );
};

export default CostCalculation;
