import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import './SignupPage.css';

const SignupPage = () => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '', role: 'User' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showVerification, setShowVerification] = useState(false);
  const [verificationCode, setVerificationCode] = useState('');
  const [verificationError, setVerificationError] = useState(null);
  const navigate = useNavigate();

  // Function to handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Function to handle verification code input
  const handleVerificationChange = (e) => {
    setVerificationCode(e.target.value);
  };

  // Function to handle form submission for signup
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('http://localhost:5000/api/auth/signup', formData);
      alert(response.data.message); // Alert the user about verification email
      setShowVerification(true); // Show the verification modal
    } catch (error) {
      console.error('Signup failed:', error.response || error);
      setError(error.response?.data?.error || 'Signup failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Function to handle email verification
  const handleVerifyEmail = async () => {
    setLoading(true);
    setVerificationError(null);
    try {
      const response = await axios.post('http://localhost:5000/api/auth/verify-email', {
        email: formData.email,
        verificationCode,
      });
      alert(response.data.message); // Alert user about successful verification
      navigate('/login'); // Redirect to login page after successful verification
    } catch (error) {
      console.error('Verification failed:', error.response || error);
      setVerificationError(error.response?.data?.error || 'Verification failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="signup-page">
      <div className="signup-header">
        <Header hideAuthButtons />
      </div>
      <div className="signup-container">
        <form onSubmit={handleSubmit}>
          <h2>Create Your Account</h2>
          {error && <p className="error">{error}</p>}
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <select name="role" value={formData.role} onChange={handleChange} className="role-select">
            <option value="User">User</option>
            <option value="Contractor">Contractor</option>
            <option value="Service Provider">Service Provider</option>
          </select>
          <button type="submit" disabled={loading}>
            {loading ? 'Signing up...' : 'Sign Up'}
          </button>
        </form>
      </div>
      <div className="signup-footer">
        <Footer />
      </div>

      {/* Verification Modal */}
      {showVerification && (
        <div className="verification-modal">
          <div className="modal-content">
            <h3>Email Verification</h3>
            <p>Please enter the verification code sent to your email:</p>
            <input
              type="text"
              placeholder="Verification Code"
              value={verificationCode}
              onChange={handleVerificationChange}
              required
            />
            {verificationError && <p className="error">{verificationError}</p>}
            <button onClick={handleVerifyEmail} disabled={loading}>
              {loading ? 'Verifying...' : 'Verify Email'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SignupPage;
