import React, { useState, useEffect } from 'react';
import './HeroSection.css';
import image1 from '../../assets/images/hero-image1.jpg'; // Import your images
import image2 from '../../assets/images/hero-image2.jpg';
import image3 from '../../assets/images/hero-image3.jpg';

const HeroSection = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const images = [image1, image2, image3]; // List of images

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length); // Loop through images
    }, 4000); // Change image every 4 seconds

    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, [images.length]);

  return (
    <section className="hero-section">
      <div
        className="hero-content"
        style={{ backgroundImage: `url(${images[currentImage]})` }} // Dynamically set background image
      >
        <h1 className="flipping-text">
          <span className="text-1">Make life easy with Innovative AI solutions for smart Real estate construction</span>
          <span className="text-2">Building the future of real estate with AI</span>
        </h1>
        <button className="hero-btn">Generate Front Elevation</button>
      </div>
    </section>
  );
};

export default HeroSection;
