import React, { useEffect } from 'react';
import './NewsPage.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import newsImage1 from '../../assets/images/news1.jpg'; // Replace with your image path
import newsImage2 from '../../assets/images/news2.jpeg'; // Replace with your image path
import newsImage3 from '../../assets/images/news3.jpg'; // Replace with your image path

const NewsPage = () => {
  const newsData = [
    {
      title: 'AI Transforming the Construction Industry',
      description: 'Artificial Intelligence (AI) is set to revolutionize the construction industry by improving efficiency and reducing costs...',
      image: newsImage1,
      link: '/news/ai-construction'
    },
    {
      title: 'Latest Trends in Real Estate Construction',
      description: 'The real estate construction industry is witnessing a surge in innovative designs and smart solutions...',
      image: newsImage2,
      link: '/news/real-estate-trends'
    },
    {
      title: 'Sustainability in Modern Architecture',
      description: 'Sustainable building practices are becoming the standard in modern architecture, promoting environmental friendliness...',
      image: newsImage3,
      link: '/news/sustainable-architecture'
    }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries, observer) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 } // Trigger animations when 10% of the element is visible
    );

    const newsCards = document.querySelectorAll('.news-card');
    newsCards.forEach(card => observer.observe(card));

    return () => {
      newsCards.forEach(card => observer.unobserve(card));
    };
  }, []);

  return (
    <div className="news-page">
      <Header />

      <div className="news-container">
        <h1 className="page-title">Latest News</h1>
        <div className="news-list">
          {newsData.map((newsItem, index) => (
            <div className="news-card" key={index}>
              <img src={newsItem.image} alt={newsItem.title} className="news-image" />
              <div className="news-content">
                <h2>{newsItem.title}</h2>
                <p>{newsItem.description}</p>
                <a href={newsItem.link} className="read-more-btn">Read More</a>
              </div>
            </div>
          ))}
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default NewsPage;
