import React, { useState } from 'react';
import './SignupPage.css';
import logo from '../../assets/images/IMG-20241014-WA0010.jpg';
import axios from 'axios';

const SignupPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'User',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRoleChange = (e) => {
    setFormData({ ...formData, role: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/signup', formData);
      alert(response.data.message); // Expect "Signup successful!"
    } catch (error) {
      console.error('Signup error:', error.response ? error.response.data : error.message);
      alert(`Error during signup: ${error.response ? error.response.data.message : error.message}`);
    }
  };
  


  return (
    <div className="signup-page">
      <header className="signup-header">
        <div className="logo">
          <img src={logo} alt="Smart Construction Logo" />
          <h1>Smart Construction</h1>
        </div>
        <nav className="nav-links">
          <a href="/">Home</a>
          <a href="/blogs">Blogs</a>
          <a href="/news">News</a>
        </nav>
      </header>
      <div className="signup-container">
        <div className="signup-box">
          <div className="left-box">
            <h2>Sign Up</h2>
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <label>Username</label>
                <input
                  type="text"
                  name="username"
                  placeholder="Enter your username"
                  value={formData.username}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="input-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="input-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="input-group role-selection">
                <label>Select Role:</label>
                <div>
                  <label>
                    <input
                      type="radio"
                      name="role"
                      value="User"
                      checked={formData.role === 'User'}
                      onChange={handleRoleChange}
                    />
                    User
                  </label>
                  <label>
                    <input
                      type="radio"
                      name="role"
                      value="Contractor"
                      checked={formData.role === 'Contractor'}
                      onChange={handleRoleChange}
                    />
                    Contractor
                  </label>
                  <label>
                    <input
                      type="radio"
                      name="role"
                      value="Service Provider"
                      checked={formData.role === 'Service Provider'}
                      onChange={handleRoleChange}
                    />
                    Service Provider
                  </label>
                </div>
              </div>
              <button type="submit" className="btn-signup">Sign Up</button>
            </form>
          </div>
          <div className="right-box">
            <h2>Welcome Back!</h2>
            <p>If you already have an account, sign in below</p>
            <a href="/login" className="btn-login">Login</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
