import React from 'react';
import './Header.css';
import logo from '../../assets/images/logo1.png';

const Header = () => {
  // Check if the user is logged in by looking for a token in localStorage
  const isLoggedIn = !!localStorage.getItem('token');

  return (
    <header className="header">
      <nav className="nav">
        <div className="logo">
          <a href="/">
            <img src={logo} alt="Smart Construction Logo" className="logo-image" />
            Smart Construction
          </a>
        </div>
        
        {/* Navigation Links */}
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/blogs">Blogs</a></li>
          <li><a href="/news">News</a></li>
          <li><a href="/service-provider">Service Provider</a></li>
          <li><a href="/contractor-login">Contractor</a></li>
        </ul>
        
        {/* Authentication Buttons - Show only if not logged in */}
        <div className="auth-buttons">
          {!isLoggedIn && (
            <>
              <a href="/login">
                <button className='btn'>
                  Login
                </button>
              </a>
              <a href="/signup">
                <button className='btn'>
                  Signup
                </button>
              </a>
            </>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
