import React, { useState } from 'react';
import './CostCalculation.css';

const CostCalculation = () => {
  const [areaSize, setAreaSize] = useState('');
  const [constructionType, setConstructionType] = useState('Grey Structure');
  const [constructionMode, setConstructionMode] = useState('Without Material');
  const [cost, setCost] = useState(null);

  const handleCalculate = () => {
    let baseCost = 1000; // Base cost per square foot (dummy data)
    let materialMultiplier = constructionMode === 'With Material' ? 1.5 : 1;
    let typeMultiplier = constructionType === 'Complete' ? 1.2 : 1;

    const totalCost = baseCost * areaSize * materialMultiplier * typeMultiplier;
    setCost(totalCost);
  };

  return (
    <div className="cost-calculation">
      <h2>Construction Cost Calculator</h2>
      <div className="form-group">
        <label>Area Size (in Sq. Ft)</label>
        <input
          type="number"
          value={areaSize}
          onChange={(e) => setAreaSize(e.target.value)}
          placeholder="Enter Area Size"
        />
      </div>
      <div className="form-group">
        <label>Construction Type</label>
        <select
          value={constructionType}
          onChange={(e) => setConstructionType(e.target.value)}
        >
          <option value="Grey Structure">Grey Structure</option>
          <option value="Complete">Complete</option>
        </select>
      </div>
      <div className="form-group">
        <label>Construction Mode</label>
        <select
          value={constructionMode}
          onChange={(e) => setConstructionMode(e.target.value)}
        >
          <option value="With Material">With Material</option>
          <option value="Without Material">Without Material</option>
        </select>
      </div>
      <button onClick={handleCalculate} className="calculate-btn">Calculate Cost</button>

      {cost && (
        <div className="cost-result">
          <h3>Total Estimated Cost: Rs. {cost.toFixed(2)}</h3>
        </div>
      )}
    </div>
  );
};

export default CostCalculation;
