// GenerateFrontElevation.jsx
import React, { useState } from 'react';
import './GenerateFrontElevation.css';
import axios from 'axios';

const GenerateFrontElevation = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleGenerateClick = async () => {
    setLoading(true);

    try {
      // Change this to your ngrok URL
      const ngrokUrl = 'http://<ngrok-id>.ngrok.io';

      // Send a POST request to the Flask server
      const response = await axios.post(
        `${ngrokUrl}/generate`,
        { plotSize: '5 Marla' },  // Modify payload if needed
        { headers: { 'Content-Type': 'application/json' } }
      );

      if (response.data.image) {
        // Update imageUrl with the base64 image received
        setImageUrl(`data:image/png;base64,${response.data.image}`);
      } else {
        console.error('No image data received');
      }
    } catch (error) {
      console.error("Error generating front elevation:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="generate-front-elevation">
      <h2>Generate Front Elevation</h2>
      <button onClick={handleGenerateClick} disabled={loading} className="generate-btn">
        {loading ? 'Generating...' : 'Generate 5 Marla Front Elevation'}
      </button>
      {imageUrl && <img src={imageUrl} alt="Generated Front Elevation" className="generated-image" />}
    </div>
  );
};

export default GenerateFrontElevation;
