import React, { useState } from 'react';
import './GenerateFrontElevation.css';
import axios from 'axios';
import Header from '../../components/Header/Header'; // Import Header component
import Footer from '../../components/Footer/Footer'; // Import Footer component

const GenerateFrontElevation = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showImageModal, setShowImageModal] = useState(false);

  const handleGenerateClick = async () => {
    setLoading(true);
    setError(null);

    try {
      // Make a request to the Flask backend
      const response = await axios.get('http://127.0.0.1:5000/generate', {
        responseType: 'blob', // Important: to handle binary data (image)
      });

      // Convert the response blob to a URL
      const imageBlob = new Blob([response.data], { type: 'image/png' });
      const imageUrl = URL.createObjectURL(imageBlob);

      setImageUrl(imageUrl); // Set the image URL to state
      setShowImageModal(true); // Show the modal with the image
    } catch (err) {
      console.error('Error generating front elevation:', err);
      setError('Failed to generate image. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const closeModal = () => {
    setShowImageModal(false);
    setImageUrl(null); // Clear the image URL
  };

  return (
    <div className="app-container">
      <Header /> {/* Use your Header component */}
      <main className="main-content">
        <div className="generate-front-elevation">
          <h2>Generate Front Elevation</h2>
          <button
            onClick={handleGenerateClick}
            disabled={loading}
            className="generate-btn"
          >
            {loading ? 'Generating...' : 'Generate 5 Marla Front Elevation'}
          </button>
          {error && <p className="error-message">{error}</p>}
        </div>

        {/* Modal for displaying the generated image */}
        {showImageModal && imageUrl && (
          <div className="image-modal">
            <div className="modal-content">
              <span className="close-btn" onClick={closeModal}>
                &times;
              </span>
              <img
                src={imageUrl}
                alt="Generated Front Elevation"
                className="generated-image"
              />
            </div>
          </div>
        )}
      </main>
      <Footer /> {/* Use your Footer component */}
    </div>
  );
};

export default GenerateFrontElevation;
