// GenerateFrontElevation.jsx
import React, { useState } from 'react';
import './GenerateFrontElevation.css';
import axios from 'axios';

const GenerateFrontElevation = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleGenerateClick = async () => {
    setLoading(true);
    try {
      // Send a request to the Flask server to generate the front elevation
      const response = await axios.post('http://127.0.0.1:5000/generate', { plotSize: '5 Marla' });
      
      // Update imageUrl with the URL of the generated image
      setImageUrl(response.data.imageUrl);
    } catch (error) {
      console.error("Error generating front elevation:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="generate-front-elevation">
      <h2>Generate Front Elevation</h2>
      <button onClick={handleGenerateClick} disabled={loading} className="generate-btn">
        {loading ? 'Generating...' : 'Generate 5 Marla Front Elevation'}
      </button>
      {imageUrl && <img src={imageUrl} alt="Generated Front Elevation" className="generated-image" />}
    </div>
  );
};

export default GenerateFrontElevation;
