import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';
import logo from '../../assets/images/IMG-20241014-WA0010.jpg';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setErrorMessage('');

        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', {
                username: email, // Adjust to match your backend field
                password,
            });

            // Save token or perform other actions after successful login
            console.log('Login successful:', response.data);
            // Redirect or set user state here
        } catch (error) {
            console.error('Login error:', error.response.data);
            setErrorMessage(error.response.data.message);
        }
    };

    return (
        <div className="login-page">
            <header className="login-header">
                <div className="logo">
                    <img src={logo} alt="Smart Construction Logo" />
                    <h1>Smart Construction</h1>
                </div>
                <nav className="nav-links">
                    <a href="/">Home</a>
                    <a href="/blogs">Blogs</a>
                    <a href="/news">News</a>
                </nav>
            </header>
            <div className="login-container">
                <div className="login-box">
                    <div className="left-box">
                        <h2>Sign In</h2>
                        {errorMessage && <p className="error-message">{errorMessage}</p>}
                        <div className="input-group">
                            <label>Email</label>
                            <input 
                                type="text" 
                                placeholder="Enter your username" 
                                value={email}
                                onChange={(e) => setEmail(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Password</label>
                            <input 
                                type="password" 
                                placeholder="Enter your password" 
                                value={password}
                                onChange={(e) => setPassword(e.target.value)} 
                            />
                        </div>
                        <button className="btn-login" onClick={handleLogin}>Sign In</button>
                    </div>
                    <div className="right-box">
                        <h2>Welcome Back!</h2>
                        <p>If you donâ€™t have an account, sign up below</p>
                        <a href="/signup" className="btn-signup">Sign Up</a>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
