import React, { useState } from 'react';
import './TrendingDesigns.css';
import image10 from '../../assets/images/trending1.jpg';
import image11 from '../../assets/images/trending2.jpg'; // Add other images
import image12 from '../../assets/images/trending3.jpg';
import image13 from '../../assets/images/trending4.jpg';
import image14 from '../../assets/images/trending5.jpg';

const TrendingDesigns = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    { src: image10, alt: "OBSIDIAN BANK" },
    { src: image11, alt: "EUREKA VISION THEATER" },
    { src: image12, alt: "ROSETON BUILDING" },
    { src: image13, alt: "TITAN PLAZA" },
    { src: image14, alt: "NEXUS TOWER" }
  ];

  const handleNext = () => {
    if (currentIndex < images.length - 3) { // Assuming 3 images visible
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <section className="trending-designs">
      <h2>Trending Designs</h2>
      <div className="slider-wrapper">
        <button className="slider-btn left-arrow" onClick={handlePrev}>
          {"<"}
        </button>
        <div className="design-list" style={{ transform: `translateX(-${currentIndex * 280}px)` }}>
          {images.map((image, index) => (
            <div className="design-card" key={index}>
              <img src={image.src} alt={image.alt} />
              <p>{image.alt}</p>
            </div>
          ))}
        </div>
        <button className="slider-btn right-arrow" onClick={handleNext}>
          {">"}
        </button>
      </div>
    </section>
  );
};

export default TrendingDesigns;
