import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null); // Reset error state before the request
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', formData); // Ensure this URL is correct
      localStorage.setItem('token', res.data.token);

      // Navigate based on user role
      if (res.data.role === 'User') {
        navigate('/UserDashboard');
      } else if (res.data.role === 'Contractor') {
        navigate('/ContractorDashboard');
      } else if (res.data.role === 'Service Provider') {
        navigate('/ServiceProviderDashboard');
      }
    } catch (error) {
      console.error('Login failed:', error); // Log the exact error for debugging
      if (error.response && error.response.data && error.response.data.message) {
        setError(error.response.data.message); // Display backend error message if available
      } else {
        setError('Login failed. Please check your credentials.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
