import React, { useState } from 'react';
import axios from 'axios';
import './SignupPage.css'; // Create your CSS similar to LoginPage
import logo from '../../assets/images/IMG-20241014-WA0010.jpg';

const SignupPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('User'); // Default role
    const [errorMessage, setErrorMessage] = useState('');

    const handleSignup = async (e) => {
      e.preventDefault();
      setErrorMessage('');
  
      try {
          const response = await axios.post('http://localhost:5000/api/auth/signup', {
              username,
              email, // Add email field
              password,
              role,
          });
  
          // Handle successful signup
          console.log('Signup successful:', response.data);
          // Redirect to login page or show success message
          // Example: Redirect to login page after signup
          window.location.href = '/login'; // Adjust this as necessary for your routing
      } catch (error) {
          // Error handling
          if (error.response) {
              console.error('Signup error:', error.response.data);
              setErrorMessage(error.response.data.message);
          } else {
              console.error('Signup error:', error.message);
              setErrorMessage('An unexpected error occurred. Please try again.');
          }
      }
  };
  
  
    return (
        <div className="signup-page">
            <header className="signup-header">
                <div className="logo">
                    <img src={logo} alt="Smart Construction Logo" />
                    <h1>Smart Construction</h1>
                </div>
            </header>
            <div className="signup-container">
                <div className="signup-box">
                    <h2>Sign Up</h2>
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                    <div className="input-group">
                        <label>Username</label>
                        <input 
                            type="text" 
                            placeholder="Enter your username" 
                            value={username}
                            onChange={(e) => setUsername(e.target.value)} 
                        />
                    </div>
                    <div className="input-group">
                        <label>Password</label>
                        <input 
                            type="password" 
                            placeholder="Enter your password" 
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} 
                        />
                    </div>
                    <div className="input-group">
                        <label>Role</label>
                        <select value={role} onChange={(e) => setRole(e.target.value)}>
                            <option value="User">User</option>
                            <option value="Contractor">Contractor</option>
                            <option value="ServiceProvider">Service Provider</option>
                        </select>
                    </div>
                    <button className="btn-signup" onClick={handleSignup}>Sign Up</button>
                </div>
            </div>
        </div>
    );
};

export default SignupPage;
