// GenerateFrontElevation.jsx
import React, { useState } from 'react';
import './GenerateFrontElevation.css';
import axios from 'axios';

const GenerateFrontElevation = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleGenerateClick = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://127.0.0.1:5000/generate', {
        plotSize: '5 Marla', // This is the input you specified
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.data.image) {
        setImageUrl(`data:image/png;base64,${response.data.image}`);
      } else {
        setError('No image data received');
      }
    } catch (err) {
      console.error("Error generating front elevation:", err);
      setError('Failed to generate image. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="generate-front-elevation">
      <h2>Generate Front Elevation</h2>
      <button onClick={handleGenerateClick} disabled={loading} className="generate-btn">
        {loading ? 'Generating...' : 'Generate 5 Marla Front Elevation'}
      </button>
      {error && <p className="error-message">{error}</p>}
      {imageUrl && <img src={imageUrl} alt="Generated Front Elevation" className="generated-image" />}
    </div>
  );
};

export default GenerateFrontElevation;
